mapper.select:
SELECT
    cd.*,
    COALESCE(
        TRIM(BOTH ' ' FROM (
            SELECT GROUP_CONCAT(DISTINCT cf.imageUrl SEPARATOR ', ') FROM camping_file cf WHERE cd.camping_contentId = cf.camping_contentId
        )),
        '' 
    ) AS imageUrl
FROM camping_details cd
WHERE cd.camping_num = #{num}
